import pigpio
import time

SERVO_GPIO = 17
PWM_FREQUENCY = 80
PWM_RANGE = 2500

pi = pigpio.pi()

if not pi.connected:
    exit()

# Set the PWM frequency and range for the servo.
pi.set_PWM_frequency(SERVO_GPIO, PWM_FREQUENCY)
pi.set_PWM_range(SERVO_GPIO, PWM_RANGE)

# Set the initial position of the servo.
pi.set_servo_pulsewidth(SERVO_GPIO, 500)

# Wait for a while...
time.sleep(0.5)

# Set the servo to a new position.
pi.set_servo_pulsewidth(SERVO_GPIO, 2500)

# Wait for a while...
time.sleep(0.5)

# Stop PWM and release resources.
pi.set_servo_pulsewidth(SERVO_GPIO, 0)
pi.stop()